var openPanel=function(c,o){c.classList.add("active"),gsap.to(o,{duration:.7,maxHeight:o.scrollHeight+"px",opacity:1,ease:"power2.out"})},closePanel=function(c,o){c.classList.remove("active"),gsap.to(o,{duration:.7,maxHeight:"0px",opacity:0,ease:"power2.in"})},managerIcon=function(){var o=document.querySelectorAll(".accordion__control");o.forEach(function(c){c.addEventListener("click",function(c){o.forEach(function(c){return c.classList.remove("control-active")}),c.currentTarget.classList.add("control-active")})})},initAccordion=function(){var e=document.querySelectorAll(".accordion__item");e.forEach(function(n){var c=n.querySelector(".accordion__control"),o=n.querySelector(".accordion__panel");c.addEventListener("click",function(){(n.classList.contains("active")?closePanel:(e.forEach(function(c){var o;c!==n&&c.classList.contains("active")&&(o=c.querySelector(".accordion__panel"),closePanel(c,o))}),openPanel))(n,o)})})};initAccordion(),managerIcon();