var managerTabs=function(){var a=document.querySelectorAll(".tabs__btn"),n=document.querySelectorAll(".tab-content");a.forEach(function(t){t.addEventListener("click",function(t){var t=t.currentTarget,e=document.querySelector('[data-target="'.concat(t.dataset.path,'"]'));a.forEach(function(t){return t.classList.remove("tabs__btn-active")}),t.classList.add("tabs__btn-active"),n.forEach(function(a){a.classList.contains("tab-content-active")&&gsap.to(a,{opacity:.5,duration:.3,onComplete:function(){var t;a.classList.remove("tab-content-active"),a.style.display="none",(t=e).style.display="block",gsap.set(t,{opacity:.5}),gsap.to(t,{opacity:1,duration:.3,onComplete:function(){t.classList.add("tab-content-active")}})}})})})})};managerTabs();